import { Component, OnInit } from '@angular/core';
import { Task } from './../../models/Task'

@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.css']
})
export class InputComponent implements OnInit {

  tasks:Task[] = [];

  inputTask: string = "";

  DateSelected: any;
  TimeSelected: any;

  fetchDateSelected(){

  }

  constructor() {}


  ngOnInit(): void {

  }

  addTask() {
    this.tasks.push({
      content: this.inputTask,
      completed: false
    });

    console.log(this.tasks);

    this.inputTask = "";
  }
}
<form id="bodyInput" (submit)="addTask();fetchDateSelected;">
    <input id="inputText" type="text" name="inputTask" placeholder="Add task here" [(ngModel)]="inputTask"/>
    <input id="inputDate" type="date" name="inputDate" [(ngModel)]="DateSelected"/>
    <input id="inputTime" type="time" name="inputTime" [(ngModel)]="TimeSelected"/>
    <input id="add" type="submit" value="Add" (click)="fetchDateSelected"/>
</form>
