@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  title: string = 'My app';

  constructor() {}

  ngOnInit(): void {

  }
}
<header>
  <h1>{{ title }}</h1>

  <button>Add</button>
</header>
