import { Component } from '@angular/core';

@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.css']
})
export class InputComponent {

}
<form [formGroup]="newTaskForm" (ngSubmit)="addTask()" id="bodyInput">
    <input formControlName="taskText" id="inputText" type="text" value="" placeholder="Add task here">
    <input formControlName="taskDate" id="inputDate" type="date">
    <input formControlName="taskTime" id="inputTime" type="time" name="">

    <button id="add" onclick="addTask()" (ngSubmit)="addTask()" type="submit">{{ button }}</button>
</form>
