import { Component, Input, Output, OnInit, EventEmitter } from '@angular/core';
import { Task } from './../../models/Task'

@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.css']
})

export class InputComponent implements OnInit {

  @Output() tasksChange = new EventEmitter();

  @Input() task!: [];

  tasks:Task[] = [];
  inputTask: string = "";
  inputDate: any;
  inputTime: any;

  constructor() {}
  ngOnInit(): void {}

  addTask() {
    this.tasks.push({
      content: this.inputTask,
      date: this.inputDate,
      time: this.inputTime,
      completed: false
    });

    console.log(this.tasks);

    this.tasksChange.emit(this.task);

    this.inputTask = "";
    this.DateSelected = "";
    this.TimeSelected = "";
  }
}
<form id="bodyInput" (submit)="addTask()">
    <input id="inputText" type="text" name="inputTask" [(ngModel)]="inputTask" placeholder="Add task here" required/>
    <input id="inputDate" type="date" name="inputDate" [(ngModel)]="inputDate" required/>
    <input id="inputTime" type="time" name="inputTime" [(ngModel)]="inputTime" required/>
    <input id="add" type="submit" value="Add"/>
</form>
